{"mcp":"{
	"servers": {
		"upstash/context7": {
			"type": "http",
			"url": "https://mcp.context7.com/mcp",
			"headers": {
				"undefined": "{context7_api_key}"
			},
			"gallery": "https://api.mcp.github.com/v0/servers/dcec7705-b81b-4e0f-8615-8032604be7ad",
			"version": "1.0.0"
		},
		"oraios/serena": {
			"type": "stdio",
			"command": "uvx",
			"args": [
				"--from",
				"git+https://github.com/oraios/serena",
				"serena",
				"start-mcp-server",
				"serena==latest",
				"--context",
				"ide-assistant"
			],
			"gallery": "https://api.mcp.github.com/v0/servers/0658e342-7ae7-493a-b378-a324363ee322",
			"version": "1.0.0"
		},
		"console-ninja": {
			"command": "npx",
			"args": [
				"-y",
				"-c",
				"node ~/.console-ninja/mcp/"
			],
			"type": "stdio"
		},
		"chromedevtools/chrome-devtools-mcp": {
			"type": "stdio",
			"command": "npx",
			"args": [
				"chrome-devtools-mcp@latest",
				"--browserUrl",
				"${input:browser_url}",
				"--headless",
				"${input:headless}",
				"--isolated",
				"${input:isolated}",
				"--channel",
				"${input:chrome_channel}"
			],
			"gallery": "https://api.mcp.github.com/2025-09-15/v0/servers/13749964-2447-4c31-bcab-32731cced504",
			"version": "0.0.1-seed"
		},
		"shadcnVue": {
      "command": "npx",
      "args": [
        "shadcn-vue@latest",
        "mcp"
      ]
    }
	},
	"inputs": [
		{
			"id": "browser_url",
			"type": "promptString",
			"description": "Optional: connect to an already-running Chrome (remote debugging / port-forward). Example: http://127.0.0.1:9222",
			"password": false
		},
		{
			"id": "headless",
			"type": "promptString",
			"description": "Run Chrome headless (true/false). Default: false",
			"password": false,
			"default": "false"
		},
		{
			"id": "isolated",
			"type": "promptString",
			"description": "Use a temporary user-data-dir (true/false). Default: false",
			"password": false,
			"default": "false"
		},
		{
			"id": "chrome_channel",
			"type": "promptString",
			"description": "Chrome channel: stable | canary | beta | dev (default: stable).",
			"password": false,
			"default": "stable"
		},
		{
			"id": "api_key",
			"type": "promptString",
			"description": "Your API key for the service",
			"password": true
		}
	]
}"}
